mapper.select:
select serial_no, tenant_id, productId, api_name, create_time,fee,customer_no
from market_merchant_api_consuming_journal
where 1 = 1

  and api_name = #{apiName}


  and productId = #{productId}


  and tenant_id = #{tenantId}


  and customer_no = #{customerNo}

order by create_time desc
